const EMPTY = ''
const VIDEO_ID_SIZE = 11


// return video id or null
export default function videoIdParser(chosenString, opts = {}) {
  // not a URL
  if (!hasTopLevelDomain(chosenString)) {
    if (chosenString.length === VIDEO_ID_SIZE) {
      return chosenString
    } else {
      return EMPTY
    }
  }

  // Sinful constants
  const WWW = 'www.'
  const HTTP = 'https://'
  // lord forgive me, we are checking if its a youtube link at all
  if (!isDotCom(chosenString)) {
    console.error('invalid youtube domain')
    return EMPTY
  }
  const shift = chosenString.includes(WWW) ? chosenString.indexOf(WWW) + WWW.length : 0
  let youtubeUrl = chosenString.substring(shift, chosenString.indexOf('/', shift))
  const filterStrings = ['youtube.com', 'm.youtube.com', 'youtu.be']

  if (!youtubeUrl.includes(HTTP)) {
    youtubeUrl = HTTP + youtubeUrl
  }
  const youtubeURLObject = new URL(youtubeUrl)

  const hostname = youtubeURLObject.hostname
  let validUrl = false
  filterStrings.every((domainName) => {
    if (hostname === domainName) {
      validUrl = true
      return false
    }
  })
  if (!validUrl) {
    console.error('not a certified youtube link üò∂‚Äçüå´Ô∏è')
    return EMPTY
  }
  /////////////////////////////
  // Once again I beg,
  let url = chosenString
  if (!url.includes(WWW)) {
    url = WWW + url
  }
  if (!url.includes(HTTP)) {
    url = HTTP + url
  }

  const params = new URL(url).searchParams

  const videoId = params.get('v')

  if (!videoId) {
    console.error('no v=')
    return ''
  }
  return videoId

}

function hasTopLevelDomain(string){
  return isDotCom(string) || string.includes('.ca') || string.includes('.net')|| string.includes('.gg')
         || string.includes('.txt') || string.includes('.gov') || string.includes('.io') || string.includes('.org')
}

function isDotCom(string) {
  return string.includes('.com')
}
