import { defineStore } from "pinia"
const actions = {
  toggleAutopause(){
    this.autopause = !this.autopause
  },
  toggleDevMode(){
    this.devMode = !this.devMode
  }
}

const state = {
  autopause: true,
  devMode: false,
}

export const useSettingStore = defineStore('settings', {
  state: () => state,

  actions
})
