import {app} from '../../main'
// import { usePlaylistStore } from "../../store/playlist";
// import {store} from "../../store";


// This does NOT WORK below
// const store = app.config.globalProperties.$store.playlist.currentSong

// payload - items
export function playSongByInput(payload) {
  const store = app.config.globalProperties.$store
  console.log(payload);
  const song = {
    name: payload.items[0].snippet.title,
    videoId: payload.items[0].id
  }

  store.playlist.changeCurrentSongBySong(song)

  console.log(song)
}
