import {store} from '../../main'
// Use index


// This does NOT WORK below
// const store = app.config.globalProperties.$store.playlist.currentSong

// payload - items
export function playSongByInput(payload) {
  console.log(payload)
  const video = {
    name: payload.items[0].snippet.title,
    videoId: payload.items[0].id
  }

  store.playlist.changeCurrentSongBySong(video)
}

export function queueSong(payload) {
  const videoId = payload.items[0].id
  const video = {
    name: payload.items[0].snippet.title,
    videoId: videoId
  }
  const duplicateVideo = store.playlist.getVideoById(videoId)

  if (duplicateVideo && Object.keys(duplicateVideo).length) {
    console.warn('Duplicate video!')
    return
  }
  store.playlist.addSongToEnd(video)
}

export function removeSong(videoId) {
  // TODO
}

export function playNextSong() {
  if (!store.playlist.currentPlaylist.length) {
    console.warn('empty playlist')
    return
  }
  // lol
  const key = Math.floor(Math.random() * store.playlist.currentPlaylist.length)

  store.playlist.changeCurrentSongInPlaylist(key)
}
