import { START_SONG_INDEX } from "../../constants/constants"

export const actions = {
  syncPlaylist(index = 0) {
    if (this.playlists.length === 0) {
      console.log('no playlists ):')
      return
    }
    this.currentPlaylist = this.playlists[index].playlist
    if (!this.currentSong.videoId) {
      this.changeSong(START_SONG_INDEX)
    }
  },
  changeSong(index = START_SONG_INDEX, removeSong = false) {
    if (this.currentPlaylist.length === 0) {
      console.log('out of songs!')
      return
    }
    const song = this.currentPlaylist[index]
    this.currentSong = song
    if (removeSong) this.removeSongByVideoId(this.currentSong.videoId)
  },
  removeSongByVideoId(videoId) {
    const song = this.currentPlaylist.find(song => song.videoId === videoId)
    const index = this.currentPlaylist.indexOf(song)
    if (index >= 0) {
      this.currentPlaylist.splice(index, 1)
    }
  }
}
