import { YOUTUBE_API_CODES } from "../../components/constants/constants";
import { YOUTUBE_API_KEY } from "../../components/constants/api-key";

export function validateVideoId(id = '16y1AkoZkmQ') {
  const videoID = id;
  const request = new XMLHttpRequest();
  request.open('GET', 'https://www.googleapis.com/youtube/v3/videos?id=' + videoID + '&key=' + YOUTUBE_API_KEY, true);

  request.onreadystatechange = (event) => {
    if (request.readyState === request.DONE) {
      const code = _isValidVideoId(request)
      return code
    }
  }

  request.send()
}

function _isValidVideoId(request) {
  if (request.status !== 200) {
    return YOUTUBE_API_CODES.GOOLE_ERROR
  }

  const responseText = JSON.parse(request.responseText)
  if (responseText.items.length) {
    return YOUTUBE_API_CODES.VALID
  } else {
    return YOUTUBE_API_CODES.INVALID
  }
}
