import { VTextField } from 'vuetify/components';
import { VSwitch } from 'vuetify/components/VSwitch';
import sharkIcon from '../assets/svg/shark-icon.svg';
import SvgIcon from './SvgIcon.vue';
export default {
  components: {
    VTextField,
    VSwitch,
    SvgIcon
  },
  data() {
    return {
      switchValue: false
    };
  },
  emits: ['onSubmit', 'toggle-switch'],
  props: {
    placeholder: {
      type: String,
      default: ''
    },
    label: {
      type: String,
      default: ''
    }
  },
  watch: {
    switchValue() {
      this.$emit('toggle-switch', this.switchValue);
    }
  },
  computed: {
    icon() {
      return sharkIcon;
    }
  },
  methods: {
    onSubmit(event) {
      const userInput = event.srcElement._value;
      if (userInput && userInput.replace(/\s/g, '').length) {
        this.$emit('onSubmit', userInput);
      }
    }
  }
};
