import { usePlaylistStore } from "./playlist"
import { useSettingStore } from "./settings"
// import { app } from "../main"

export default {
  install: (app, options) => {
    const playlist = usePlaylistStore()
    const setting = useSettingStore()
    const store = {
      playlist,
      setting
    }
    app.config.globalProperties.$store = store
  }
}

