import { mapState } from 'pinia';
import { usePlaylistStore } from '../store/playlist';
import { VDivider, VBtn, VChip } from 'vuetify/components';
export default {
  components: {
    VDivider,
    VBtn,
    VChip
  },
  computed: {
    ...mapState(usePlaylistStore, ['currentPlaylist', 'currentSong', 'currentPlaylist'])
  },
  methods: {
    onDelete(song) {
      this.$store.playlist.removeSongByVideoId(song.videoId);
    },
    onPlay(song) {
      this.$store.playlist.changeCurrentSongBySong(song);
    }
  }
};
