import { EMPTY, VIDEO_ID_SIZE, WWW, HTTPS } from "../constants/constants"

export default function videoIdParser(chosenString) {
  if (!hasTopLevelDomain(chosenString)) {
    if (chosenString.length === VIDEO_ID_SIZE) {
      return chosenString
    } else {
      return EMPTY
    }
  }

  if (!hasDotComDomain(chosenString)) {
    return EMPTY
  }

  if (!isValidYoutubeUrl(chosenString)) {
    console.error('not a certified youtube link ðŸ˜¶â€ðŸŒ«ï¸')
    return EMPTY
  }

  const videoId = getVideoIdParam(chosenString)
  return videoId
}

function getVideoIdParam(urlString) {
  let url = urlString
  if (!url.includes(WWW)) {
    url = WWW + url
  }
  if (!url.includes(HTTPS)) {
    url = HTTPS + url
  }

  const params = new URL(url).searchParams

  const videoId = params.get('v')
  return videoId
}

function isValidYoutubeUrl(urlString) {
  const shift = urlString.includes(WWW) ? urlString.indexOf(WWW) + WWW.length : 0

  let youtubeUrl = urlString.substring(shift, urlString.indexOf('/', shift))
  const filterStrings = ['youtube.com', 'm.youtube.com', 'youtu.be']

  if (!youtubeUrl.includes(HTTPS)) {
    youtubeUrl = HTTPS + youtubeUrl
  }
  const youtubeURLObject = new URL(youtubeUrl)

  const hostname = youtubeURLObject.hostname
  let validUrl = false
  filterStrings.every((domainName) => {
    if (hostname === domainName) {
      validUrl = true
      return false
    }
  })
  return validUrl
}

function hasTopLevelDomain(string) {
  return hasDotComDomain(string) || string.includes('.ca') || string.includes('.net') || string.includes('.gg')
    || string.includes('.txt') || string.includes('.gov') || string.includes('.io') || string.includes('.org')
}

function hasDotComDomain(string) {
  return string.includes('.com')
}
