import { usePlaylistStore } from "../../store/playlist";
const playStore = usePlaylistStore()

// payload - items


export function playSongByInput(payload) {
  const song = {
    name: payload[0].snippet.title,
    videoId: payload[0].id
  }
  playStore.changeCurrentSongBySong(song)
}
