const KEY = 'AIzaSyDC5m0zYh3K6erncCUBGw9AXhAAf4wV8o8'

function validateVideoId(id = 'ZZZ16y1AkoZkmQ') {
  const videoID = id;

  const request = new XMLHttpRequest();
  request.open('GET', 'https://www.googleapis.com/youtube/v3/videos?id='+ videoID + '&key=' + KEY, true);
  request.onreadystatechange = (prop) =>{
    if (request.readyState === request.DONE) {
      console.log(request)
    }
  }
  request.send()

}

export default function videoIdParser(chosenString, opts = {}) {
  validateVideoId()
  // potential types of strings:
  // https://www.youtube.com/watch?v=suSuy8Sg-TY&list=RD_83LZ79VMLg&index=4&ab_channel=MrMoMMusic
  // normal 'FU-pEDpOPeU'

}
