import { defineStore } from "pinia"
const state = {
  playlists: [
    {
      id: 3,
      name: 'first playlist',
      playlist: ['id1', 'id2', 'id3']
    },
    {
      id: 10,
      name: 'second playlist',
      playlist: ['id69', 'id420', 'id4']
    }
  ],
}

const getters = {
  getPlaylists: (state) => {
    return state.playlists
  },
  getPlaylistById: (state) => {
    return (id) => {
      state.playlist.forEach((playlist) => {
        if (playlist.id === id) {
          return playlist
        }
      })
    }
  }
}

const actions = {

}

export const usePlaylistStore = defineStore('playlist', {
  state: () => state,
  getters,
  actions
})
