const KEY = 'AIzaSyDC5m0zYh3K6erncCUBGw9AXhAAf4wV8o8'

function validateVideoId(id = '16y1AkoZkmQ') {
  const videoID = id;

  const request = new XMLHttpRequest();
  request.open('GET', 'https://www.googleapis.com/youtube/v3/videos?id='+ videoID + '&key=' + KEY, true);

  request.onreadystatechange = (event) =>{
    if (request.readyState === request.DONE) {
      onReadyState(request)
    }
  }

  request.send()
}



function onReadyState(request) {
  if(request.status !== 200) {
    console.error('Google API ded')
  }

  const responseText = JSON.parse(request.responseText)
  if (responseText.items.length) {
    console.log('VALID videoID')
  } else {
    console.log('INVALID videoID')
  }
}

