import { YOUTUBE_API_CODES } from '../constants/constants'
import { YOUTUBE_API_KEY } from "../constants/api-key"

export function fetchVideoInformation(id = 'lxF31-mHLz0') {
  return new Promise((resolve) => {
    const videoID = id
    const request = new XMLHttpRequest()

    request.open('GET', 'https://www.googleapis.com/youtube/v3/videos?part=snippet%2CcontentDetails%2Cstatistics&id='
      + videoID + '&key=' + YOUTUBE_API_KEY, true)

    request.onreadystatechange = () => {
      if (request.readyState === request.DONE) {
        const code = validateVideoIdState(request)
        console.log(code);
        resolve(code)
      }
    }

    request.send()
  })
}

function validateVideoIdState(request) {
  if (!request || request.status < 200 || request.status >= 300) {
    return YOUTUBE_API_CODES.GOOGLE_ERROR
  }

  const responseText = JSON.parse(request.responseText)
  // console.log(responseText)

  if (responseText.items && responseText.items.length) {
    const payload = {
      status: YOUTUBE_API_CODES.VALID,
      items: responseText.items
    }
    return payload
  } else {
    return { status: YOUTUBE_API_CODES.INVALID }
  }
}
