import { defineStore } from "pinia"
const state = {
  playlists: [
    {
      name: 'first playlist',
      playlist: ['id1', 'id2', 'id3']
    },
    {
      name: 'second playlist',
      playlist: ['id69', 'id420', 'id4']
    }
  ],
}

const getters = {
  getPlaylists: (state) => {
    return state.playlists
  }
}

const actions = {
  getPlaylistById(id) {
    console.log(this.playlists)
  }
}

export const usePlaylistStore = defineStore('playlist', {
  state: () => state,
  getters,
  actions
})
