import { defineStore } from "pinia"
import getters from './getters'
import {actions} from './actions'
const state = {
  playlists: [
    {
      id: 1,
      name: 'first playlist',
      playlist: [
        {
          index: 1,
          videoId: '16y1AkoZkmQ',
          name: 'Rasputin'
        },
        {
          index: 2,
          videoId: 'Qaar0K5ThcM',
          name: `Let's stay the same`
        }, {
          index: 3,
          videoId: 'pnjRMmrGEjU',
          name: 'Younger you'
        },
      {
          index: 4,
          videoId: 'lxF31-mHLz0',
          name: 'Questions'
      }]
    }
  ],
  currentPlaylist: [],
  currentSong: {}
}

export const usePlaylistStore = defineStore('playlist', {
  state: () => state,
  getters,
  actions
})
