export default function videoIdParser(chosenString, opts = {}) {
  // not a URL
  if (!chosenString.includes('.com')) {
    return chosenString
  }

  // lord forgive me
  const WWW = 'www.'
  const shift = chosenString.includes(WWW) ? chosenString.indexOf(WWW) + WWW.length : 0
  let youtubeUrl = chosenString.substring(shift, chosenString.indexOf('/', shift))
  const filterStrings = ['youtube.com', 'm.youtube.com', 'youtu.be']
  if (!youtubeUrl.includes('https://')) {
    youtubeUrl = 'https://' + youtubeUrl
  }
  const youtubeURLObject = new URL(youtubeUrl)

  const hostname = youtubeURLObject.hostname
  let validUrl = false
  filterStrings.every((domainName) => {
    if (hostname === domainName) {
      validUrl = true
      return false
    }
  })
  if (!validUrl) {
    console.error('not a certified youtube link')
  }
  /////////////////////////////
  // Once again I beg
  let url
  if (!url.includes(WWW)) {
    url = WWW + url
  }

  const params = new URL(chosenString).searchParams

  const videoId = params.get('v')

  if (!videoId) {
    console.log('no v=')
  }
  return videoId

}
