import { loadYouTubeAPI } from '../utility/youtube';
export default {
  name: 'YoutubeVideo',
  data() {
    return {
      date: new Date(),
      player: null
    };
  },
  props: {
    playerOptions: {
      type: Object,
      default: () => {}
    },
    videoId: {
      type: String,
      default: '7aMOurgDB-o'
    },
    autoPause: {
      type: Boolean,
      default: false
    }
  },
  mounted() {
    this.loadYoutubePlayer();
  },
  methods: {
    onYoutubeAPIReady() {
      const playerOptions = {
        height: '500px',
        width: '500px',
        videoId: this.videoId,
        playerVars: {
          'controls': 1,
          'rel': 0,
          'autoplay': 0
        },
        events: {
          'onReady': this.onPlayerReady,
          'onStateChange': this.onPlayerStateChange
        }
      };
      this.player = new YT.Player('player', playerOptions);
    },
    isYoutubeAPILoaded() {
      return window.YT && window.YT.loaded;
    },
    unstoppable() {
      setInterval(() => {
        this.player.playVideo();
      }, 10);
    },
    onPlayerStateChange(event) {
      if (event.data === 0) {
        // play next video here
        console.warn('Video Ended');
      }
    },
    onVideoIdChange() {
      if (!this.player) {
        this.loadYoutubePlayer();
      } else {
        this.player.loadVideoById(this.videoId);
        if (this.autoPause) {
          this.player.stopVideo();
        }
      }
    },
    loadYoutubePlayer() {
      loadYouTubeAPI();
      window.onYouTubeIframeAPIReady = () => {
        this.onYoutubeAPIReady();
      };
    }
  },
  watch: {
    player() {
      // Youtube gets rekted on hot reloads
      this.loadYoutubePlayer();
      //
    },

    videoId() {
      this.onVideoIdChange();
    }
  }
};
